sp_header_footer.link:
function(scope, element) {	
	$(element).on('click.nav','.navbar-collapse.in',function(e) {	
		var target = $(e.target);
		if (target.is('a')) {
			// collapse mobile nav if clicked an anchor
			$(this).removeClass('in').addClass('collapse');
			return;
		}

		// if neither target nor parent is an anchor, do nothing
		if (!target.parent().is('a'))
			return;

		// collapse mobile nav if target is part of a submenu
		if (target.parents("ul.dropdown-menu").length > 0)
			$(this).removeClass('in').addClass('collapse');
	});
}

sp_header_footer.script:
// Defines the support queue ID that will be linked to by the Live Chat link in header
data.connect_support_queue_id = $sp.getValue('sp_chat_queue');
data.login_page = $sp.getValue('login_page');
data.is_cloud_user = false;
data.is_cloud_admin = false;
data.is_group_admin = false;
data.is_admin = false;
data.view_value = gs.getUser().getPreference('cloud_user_portal_view') || 'mine';
if(input && input.view )
{
	gs.getUser().savePreference('cloud_user_portal_view',input.view);
}
var menu = $sp.getValue("sp_rectangle_menu");
data.menu = $sp.getWidgetFromInstance(menu);
if (data.menu && data.menu.data) {
	data.menu.data.replace = true;
	// Hide login if menu already has link to login
	data.hasLogin = false;
	if (data.menu.data.menu.items) {
		for(var i in data.menu.data.menu.items) {
			var item = data.menu.data.menu.items[i];
			if (item.type == 'page' && item.sp_page == data.login_page)
				data.hasLogin = true;
		}
	}
}

var gr = new GlideRecord('sys_user_has_role');
gr.addQuery('user',gs.getUserID());
var qc = 'role.name=admin^ORrole.name=sn_cmp.cloud_service_user^ORrole.name=sn_cmp.cmp_root_admin';
gr.addEncodedQuery(qc);
gr.query();
if(gr.next()){
	data.is_cloud_user = true;
}

var gr2 = new GlideRecord('sys_user_has_role');
gr2.addQuery('user',gs.getUserID());
var qc = 'role.name=sn_cmp.cloud_group_admin';
gr2.addEncodedQuery(qc);
gr2.query();
if(gr2.next()){
	data.is_group_admin = true;
}

var gr3 = new GlideRecord('sys_user_has_role');
gr3.addQuery('user',gs.getUserID());
var qc = 'role.name=admin^ORrole.name=sn_cmp.cmp_root_admin';
gr3.addEncodedQuery(qc);
gr3.query();
if(gr3.next()){
	data.is_admin = true;
}

var gr1 = new GlideRecord('sys_user_has_role');
gr1.addQuery('user',gs.getUserID());
