sp_widget.client_script:
function($scope, $location) {  /* widget controller */  
	var c = this;
	c.sys_id = $location.search().sys_id;

	c.isActiveItem = function(item_sys_id){
		if (c.sys_id == item_sys_id) {
			return true;
		}
		else {
			return false;
		}
	};

		$scope.goTo = function(sys_id){
		var info = {};
		info.id = sys_id;
		c.sys_id = sys_id;
		$rootScope.$broadcast("cloudCategoriesEvent",info);
	}
}

