}

sp_widget.link:
function (scope,el,attr,ctrl) {
	$(document).ready(function(){
		resize_window();
	});

	$(window).resize(function(){
		resize_window();
	});

	function resize_window(){
		var panel_height = ($(document).height()- 178) + 'px';
		$(el).find('.panel').css('height',panel_height);
	}
}

sp_widget.script:
(function() {
	// populate the 'data' object
	var cat = {};
	data.categories = [];
	data.sc_catalog = "b029d6da0b212200eff7ef9bb4673a42";// cloud service catalog

	var sc = new GlideRecord('sc_category');
	sc.addQuery('sys_class_name', 'sc_category');
	sc.addQuery('sc_catalog',data.sc_catalog);
	sc.addActiveQuery();
	sc.orderBy('title');
	sc.query();
	while (sc.next()) {
		if (!$sp.canReadRecord("sc_category", sc.getUniqueValue()))
			continue;
		cat = {
			title : sc.title.getDisplayValue(),
			sys_id : sc.getUniqueValue(),
			catalog : sc.sc_catalog.getDisplayValue()
		}
		cat.count = 0;
		var itemCat = new GlideRecord("sn_cmp_bp_cat_item");
		itemCat.addQuery("category", sc.getUniqueValue());
		itemCat.addActiveQuery();
		itemCat.query();
		while (itemCat.next()) {
			if (!$sp.canReadRecord("sn_cmp_bp_cat_item", itemCat.getUniqueValue()))
				continue;
			cat.count++ ;
		}
		data.categories.push(cat);
	}
})()
