sp_widget.client_script:
function GetHelp($scope, $rootScope, $location) {
	var c = this;

	c.showCommunity = function(){
		window.open('https://community.servicenow.com/community/operations-management','_blank');			
	};

	$scope.openCommunity = function(){
		window.open($scope.data.communityUrl,'_blank');
	};

	$scope.openDocs = function(){
		window.open($scope.data.docs,'_blank');
	};

	$scope.openTutorials = function(){
		window.open($scope.data.tutorialsLink,'_blank');
	};

}

