sp_widget.link:
function(scope,elem, attrs, ctrl) {
	var panel_height;

	scope.timeout(function(){
		resize_window();
	});

	$(document).ready(function(){
		resize_window();
	});

	$(window).resize(function(){
		resize_window();
	});

	function resize_window(){
		var panel_height = ($(document).height()- 106) + 'px';
		$(elem).find('#alert_configurations').css('height',panel_height);

	}
}
