sys_script_include.script:
var CMPDeleteOpImpleStepInputParamAction = Class.create();
CMPDeleteOpImpleStepInputParamAction.prototype = Object.extendsObject(sn_smd.SMBaseAction, {
     doAction: function(params) {
		try{
			var gr;
			var getRemoveList = JSON.parse(params.postData);
			var getRemoveListLength = (getRemoveList) ? getRemoveList.length : 0;
			for(var i=0; i < getRemoveListLength; i++) {
				gr = new GlideRecord('sn_cmp_rb_keyvalue');
				if(getRemoveList[i] || getRemoveList[i] != "")
					gr.addQuery("sys_id", getRemoveList[i]);
				gr.query();
				if (gr.next()) {
					gr.deleteRecord();
				}
			}
			return true;

		} catch (error) {
			var e = error;
			throw sn_smd.SMErrorHelper.createScriptAPIError(
				gs.getMessage("Failed to delete resource block operations step input parameters"),
				"CMPDeleteOpImpleStepInputParamAction.doAction(): Failed to delete resource block operations step input parameters",
				error
			);
		}
    },
    type: 'CMPDeleteOpImpleStepInputParamAction'
});
