var CloudMatch = Class.create();
CloudMatch.prototype = {
    initialize: function() {
    },

	/**
	* Method for insertion of custom CloudMatch check.
	* Return type sample:
		var errors = []; 
		var list = {};
		list['match'] = match; //boolean result
		list['errors'] = errors; //list of error strings
	*/
	examine: function(orderId, match) {
		var errors = []; 
		var list = {};
		list['match'] = match;
		list['errors'] = errors;

		return list;
    },

    type: 'CloudMatch'
};
