// get requested_for user_id
workflow.scratchpad.requested_for = current.request.requested_for + '';

setApprovals();


function setApprovals() {
	workflow.scratchpad.groupId = '';
	workflow.scratchpad.userId = '';
	workflow.scratchpad.managerId = '';
	if(workflow.inputs.approval) {
		var approval = {};
		approval = new global.JSON().decode(workflow.inputs.approval);
		if(approval.managerApproval){
			// retrieve manager of the requester
			var gr = new GlideRecord('sys_user');
			if(gr.get(workflow.scratchpad.requested_for)) {
				workflow.scratchpad.managerId = gr.manager + '';
			}
		}
		if(approval.userApproval){
			// Retrieve list of users
			workflow.scratchpad.userId = approval.userId;
		}
		if(approval.groupApproval){
			workflow.scratchpad.groupId = approval.groupId + '';
		}
	}
}

