wf_workflow_version.on_cancel:
// This script executes if the workflow is cancelled.
// The associated job and its in_progress log(s) are marked as Finished/failure

var wfGr = new GlideRecord("wf_context");
if (wfGr.get(context_sys_id)) {
  var sp = wfGr.getValue('scratchpad');
  if (!gs.nil(sp)) {
    spJson = global.JSON.parse(sp);
    if (!gs.nil(spJson) && !gs.nil(spJson.activityConfig)) {
      var jobId = spJson.activityConfig.jobConfigId;
      if (!gs.nil(jobId)) {
        var jobGr = new GlideRecord('sn_cmp_billing_schedule');
        if (jobGr.get(jobId)) {
          jobGr.current_job_status = 'Finish';
		  jobGr.update();
          var jobLogGr = new GlideRecord('sn_cmp_billing_job_log');
          jobLogGr.addQuery('job_config', jobId);
          jobLogGr.addQuery('state', 'in_progress');
          jobLogGr.query();
          while(jobLogGr.next()) {
            jobLogGr.state = "failure";
            var details = jobLogGr.details;
            jobLogGr.details = "Workflow was cancelled. Job details at cancellation: "+details;
            jobLogGr.update();
          }
        }
      }
    }
  }
}

