function ifScript() {
	var sp = workflow.scratchpad;
	if (sp.activityConfig.currentYear < sp.activityConfig.endYear ||
		(sp.activityConfig.currentYear == sp.activityConfig.endYear &&
		 sp.activityConfig.currentMonth <= sp.activityConfig.endMonth)) {
		updatePreviousReportDownloadLog(sp);
		createNextReportDownloadLog(sp);
        return 'yes';
	}
	return 'no';
}

function updatePreviousReportDownloadLog(sp){
	var logGr = new GlideRecord('sn_cmp_billing_job_log');
	logGr.get(sp.activityConfig.logId);
	logGr.end_time = new GlideDateTime().getDisplayValue();
	logGr.state = 'success';
	logGr.details='Import complete';
	logGr.update();	
}

function createNextReportDownloadLog(sp){
	
	var gr = new GlideRecord('sn_cmp_billing_job_log');
	gr.initialize();
	
	gr.job_config = sp.activityConfig.jobConfigId;
	gr.year=sp.activityConfig.currentYear;
	gr.month=sp.activityConfig.currentMonth;
	gr.start_time=new GlideDateTime().getDisplayValue();
	gr.state='in_progress';
	gr.details='Downloading data';
	gr.wf_context=sp.activityConfig.masterWfContextId;
	
	var sysId = gr.insert();	
	sp.activityConfig.logId=sysId;
}

