run();

function run(){
	
	// find the record from job config table.
	var configGr = new GlideRecord('sn_cmp_billing_schedule');
	configGr.get(workflow.inputs.u_config_id);
	
	workflow.scratchpad.activityConfig = getActivityConfig(configGr);
	initCurrentMonth();
	createReportDownloadLog();
	updateJob();

	var cfg = workflow.scratchpad.activityConfig;	
}

function createReportDownloadLog(){
	
	var gr = new GlideRecord('sn_cmp_billing_job_log');
	gr.initialize();
	
	gr.job_config = workflow.scratchpad.activityConfig.jobConfigId;
	gr.year=workflow.scratchpad.activityConfig.currentYear;
	gr.month=workflow.scratchpad.activityConfig.currentMonth;
	gr.start_time=new GlideDateTime().getDisplayValue();
	gr.state='in_progress';
	gr.wf_context=workflow.scratchpad.activityConfig.masterWfContextId;
	gr.details='Downloading data';
	
	var sysId = gr.insert();	
	workflow.scratchpad.activityConfig.logId=sysId;
}

function updateJob(){
	
	var gr = new GlideRecord('sn_cmp_billing_schedule');
	if (gr.get(workflow.scratchpad.activityConfig.jobConfigId)) {
		gr.current_job_status = 'In Progress';	
		gr.update();
	}
}


function getActivityConfig(configGr){
	
	var config = {};
	config.accountNum = configGr.account.account_id;
	config.provider = configGr.getValue('provider');
	config.importTable = new CmpBillingJobUtil().getProviderProperty(
		configGr.provider, 'billing_import_table');
	config.jobConfigId=configGr.sys_id;
	config.bucket=configGr.bucket;
	config.clearExistingData = workflow.inputs.u_clear_existing_data;
	config.includeZeroCostItems = workflow.inputs.u_include_zero_cost_items;
	config.startYear=getYear('u_start_year');
	config.startMonth=getMonth('u_start_month');
	config.endYear=getYear('u_end_year');
	if (config.endYear < config.startYear) {
		config.endYear = config.startYear;
	}
	config.endMonth=getMonth('u_end_month');
	if (config.endMonth < config.startMonth && config.endYear == config.startYear) {
		config.endMonth = config.startMonth;
	}	
	config.masterWfContextId=getWFContextId();
	
	return config;
}

function initCurrentMonth() {
	var sp = workflow.scratchpad;
	sp.activityConfig.currentMonth = sp.activityConfig.startMonth;
	sp.activityConfig.currentYear = sp.activityConfig.startYear;
	
	if (needToFinishLastMonth(sp.activityConfig.currentMonth, sp.activityConfig.currentYear)) {
		var timeUtil = new CmpBillingDateTimeUtil(sp.activityConfig.currentYear,
												  sp.activityConfig.currentMonth);
		timeUtil.subtractMonths(1);
		sp.activityConfig.currentMonth = timeUtil.getNewPastMonth();
		sp.activityConfig.currentYear = timeUtil.getNewPastYear();
		
		// By default, clear and re-download all data when finalizing a previous month
		if ('true' === gs.getProperty('sn_cmp.billing.clear_past_months', 'true').toLowerCase())
			sp.activityConfig.clearExistingData = true;
	}
}

function getMonth(fieldName){

	var dt = new CmpBillingDateTimeUtil();	
	if(!gs.nil(workflow.inputs[fieldName]))
		return dt.padMonth( workflow.inputs[fieldName] );
	
	return dt.getCurrentMonth();
}

function getYear(fieldName){
	
	if(!gs.nil(workflow.inputs[fieldName]))
		return workflow.inputs[fieldName];
	
	var dt = new CmpBillingDateTimeUtil();
	return dt.getCurrentYear();
}

function getWFContextId(){
	
	var sys_id = current.sys_id;
	var gr = new GlideRecord('wf_context');
	gr.addQuery('id',sys_id);
	gr.query();
	gr.next();
	return gr.sys_id;
}

function needToFinishLastMonth(month, year){
	
	// Never trigger earlier months when called on demand from the UI
	if (!gs.nil(workflow.inputs.u_start_month))
		return false;
	
	var timeUtil = new CmpBillingDateTimeUtil(year,month);
	timeUtil.subtractMonths(1);
	var foundlastMonth = foundRecord(timeUtil.getNewPastYear(), timeUtil.getNewPastMonth(), '01');
	
	// if nothing found, we don't have to run..
	if(!foundlastMonth)
		return false;
	
	// true if the last month's report found, and no jobs run more than 2 days into this month
	var foundThisMonth = foundRecord(year, month, '03');
	return !foundThisMonth;
}

function foundRecord(year,month,day){

	var startedAfter = new GlideDateTime();
	startedAfter.setValueUTC(year+'-'+month+'-'+day+' 00:00:00', "yyyy-MM-dd HH:mm:ss");
	
	var gr = new GlideRecord('sn_cmp_billing_job_log');
	gr.addQuery('job_config',workflow.scratchpad.activityConfig.jobConfigId);
	gr.addQuery('year',year);
	gr.addQuery('month',month);
	gr.addQuery('state', "success");
	gr.addQuery('start_time', '>', startedAfter);
	gr.orderBy('start_time');
	gr.query();
	return gr.next();
}

