var userBudget = new sn_cmp.CMPBudgetBasedNotification().getUserBillingBudget();
var groupBudget = new sn_cmp.CMPBudgetBasedNotification().getGroupBillingBudget();

function updateJob(success, download_result){
	
	var now = new GlideDateTime().getDisplayValue();
	var gr = new GlideRecord('sn_cmp_billing_schedule');
	gr.get(workflow.scratchpad.activityConfig.jobConfigId);
	gr.last_executed_on = now;
		
	var logGr = new GlideRecord('sn_cmp_billing_job_log');
	logGr.get(workflow.scratchpad.activityConfig.logId);
	logGr.end_time = now;
	
	if(success){
		gr.last_executed_state = 'success';	
		gr.last_successful_on = gr.last_executed_on;
		logGr.state = 'success';
		logGr.details = 'Import complete';
	}
	else {
		gr.last_executed_state = 'failure';
		logGr.state = 'failure';
		var details = logGr.getValue('details');
		if (details.startsWith("Downloading data")) {
			logGr.details = details.substring(16); // trim "Downloading data" from the front	
		} else if (details.startsWith("Parsing downloaded data") ||
				  (details.startsWith("Generating reports"))) {
			var endPct = details.indexOf("%)");
			if (endPct >= 0)
				logGr.details = details.substring(endPct+2);
		}
		if (!gs.nil(download_result))
			logGr.details += download_result.error_messages;
		if (gs.nil(logGr.details))
			logGr.details = "Workflow failed or cancelled"
	}
	gr.current_job_status = 'Finish';
	
	gr.update();
	logGr.update();	
	gs.info('Updating the last executed state to ' + gr.last_executed_state);
	return gr.last_executed_state;
}

function _isSuccess(download_result){
	return !gs.nil(download_result) 
		&& !gs.nil(download_result.result)
		&& ''+download_result.result == "success";
}

var download_result = global.JSON.parse(workflow.scratchpad.download_result);
var success = _isSuccess(download_result);
activity.result = updateJob(success, download_result);

