sys_variable_value.value:
// Make policy execution call to check if change approval is required
///var policyUtil = new sn_cmp.CloudPolicyUtil();
var cloudUtil = new sn_cmp.CloudChangeUtil();


workflow.scratchpad.transformedPayload  = new global.JSON().decode(workflow.inputs.approval);
var transformedPayload = workflow.scratchpad.transformedPayload;

var assignmentGroupId = "";
var errorMsg = "";
var CHANGE_TYPE = {
	NORMAL : '' + gs.getProperty('sn_cmp.cloud_change_type_for_approval', 'normal'),
	STANDARD : '' +  gs.getProperty('sn_cmp.cloud_change_type_for_nonapproval', 'standard')
};
var STATES = {
	YES : 'yes',
	NO : 'no',
	ERROR : 'error'
};

if (!gs.nil(transformedPayload)) {
	if (_isApprovalRequired()) {
		assignmentGroupId = _getGroupId();
		if(gs.nil(assignmentGroupId)){
			activity.result = STATES.ERROR;
			errorMsg = "Assignment group could not be found. ";
		}else {
			activity.result = STATES.YES;
		}
	} else {
		activity.result = STATES.NO;
	}
} else {
	activity.result = STATES.ERROR;
	errorMsg = "Error in executing policy.";
}
switch(activity.result+''){
	case 'yes' :
		workflow.scratchpad.result.isChangeApprovalRequired = true;
		workflow.scratchpad.result.changeId = cloudUtil.createChange(workflow.inputs.reqid, CHANGE_TYPE.NORMAL, assignmentGroupId, workflow.scratchpad.operation_name,workflow.scratchpad.resource_name, workflow.scratchpad.configuration_item);
		workflow.scratchpad.change_event_id = workflow.scratchpad.result.changeId+"chng"; // when approval status on the change record is changed, a business rule is fired with this change_event_id
		break;
	case 'no' :
		workflow.scratchpad.result.isChangeApprovalRequired = false;
		workflow.scratchpad.result.changeId = cloudUtil.createChange(workflow.inputs.reqid, CHANGE_TYPE.STANDARD, assignmentGroupId, workflow.scratchpad.operation_name,workflow.scratchpad.resource_name, workflow.scratchpad.configuration_item);
		break;
	default :
		workflow.scratchpad.result.error = true;
		workflow.info(errorMsg);
}

function _isApprovalRequired() {
	// Checks if change approval is needed.
	return transformedPayload.groupApproval;
}

function _getGroupId() {
	// returns assignment group ID to which change request will be assigned.
	return transformedPayload.groupId;
}

/**
 * To create change record
 *
 * @param changeType
 *            Change type to create
 * @param assignment_group
 *            The change is assigned to this group.
 * @returns String
 *            Change Record sys_id
 */
