var gr = new GlideRecord('sc_req_item');
if(gr.get(workflow.inputs.reqid)) {
	workflow.scratchpad.configuration_item = gr.variables.resourceId;
	workflow.scratchpad.operation_name = gr.variables.operationName;
	workflow.scratchpad.resource_id = gr.variables.resourceId;
	if(!gs.nil(gr.configuration_item + '')){
		workflow.scratchpad.resource_name = gr.configuration_item.name+'';
	}
}


// Set result error to false by default
workflow.scratchpad.result = {
	error: false,
	changeId : '',
	changeStatus : '',
	isChangeApprovalRequired : false
};

var gr = new GlideRecord('sc_req_item');
if(gr.get(workflow.inputs.reqid)) {
	workflow.scratchpad.configuration_item = gr.variables.resourceId;
	workflow.scratchpad.operation_name = gr.variables.operationName;
