// Set CR to scheduled if no approval is needed

var changeGr = new GlideRecord('change_request');
changeGr.get(workflow.scratchpad.result.changeId + '');

var changeRequest = new global.ChangeRequest(changeGr);
changeRequest.setScheduled();
changeRequest.update();

// Set CR to scheduled if no approval is needed

var changeGr = new GlideRecord('change_request');
changeGr.get(workflow.scratchpad.result.changeId + '');

var changeRequest = new global.ChangeRequest(changeGr);
changeRequest.setScheduled();
changeRequest.update();

// Check approval status on the change record
var changeGr = new GlideRecord('change_request');
if(changeGr.get(workflow.scratchpad.result.changeId + '')){
	var change = new global.ChangeRequest(changeGr);
	if(change.isCanceled()){
		activity.result = "cancelled";
		workflow.scratchpad.result.changeStatus = activity.result;
	}
	if(change.isApproved()){
		activity.result = "approved";
		workflow.scratchpad.result.changeStatus = activity.result;
	}
	if(change.isRejected()){
		activity.result = "rejected";
		workflow.scratchpad.result.changeStatus = activity.result;
	}
}
createCloudTrail('Cloud Operation Change Request WF: Change Approval Status :' + workflow.scratchpad.result.changeStatus, '');

function createCloudTrail(statusMessageSummary,statusMessageDetails) {
	var os = new sn_cmp_api.CloudTrailServiceScript();
	workflow.scratchpad.cloudTrail = generateCloudTrailJSON(statusMessageSummary,statusMessageDetails);
	var input = new global.JSON().encode(workflow.scratchpad.cloudTrail);
	var output = os.createTrail(input);
}

function generateCloudTrailJSON(statusMessageSummary,statusMessageDetails){
	var result = {};
	var gr = new GlideRecord('sc_req_item');
    if(gr.get(workflow.inputs.reqid)) {
		result.catalogItemId = '' + gr.cat_item;
		result.requestItemId = '' + gr.sys_id;
		result.invocationTemplateId = '' + gr.sys_id;
		result.userId = gr.request.requested_for + '';
	}
	result.orderId = '';
	result.stackId = '' + workflow.scratchpad.resource_id;
	result.contextKey  = '';
	result.contextTable = 'sc_req_item';
	
	result.targetInstanceId = '' + workflow.scratchpad.resource_id;
	
	result.groupId = '';
	result.messageType = '2'; // info
	result.milestone = true;
	result.step = 'Apply Policy Rule Action';
	result.statusMessageSummary = statusMessageSummary; 
	result.statusMessageDetails = statusMessageDetails; 
    return result;
}

// Check approval status on the change record
var changeGr = new GlideRecord('change_request');
if(changeGr.get(workflow.scratchpad.result.changeId + '')){
