sys_variable_value.value:
activity.result = 'skip';

invokePolicy();

function invokePolicy() {
	var nextFlow = '';
	var invokeApprovalWFPolicy = false;

	// Check if approval is required by invoking RequestTask Policy
	var policyUtil = new sn_cmp.CloudPolicyUtil();
	var object = policyUtil.invokePolicy(current, "", "taskRemediation" /*TODO*/, "provision" /*TODO: Differentiate between provision vs lifecycle opr */, "blueprint", current.cat_item.blue_print + '');
	workflow.scratchpad.approvalPayload  = new global.JSON().decode(object);

	if(workflow.scratchpad.approvalPayload != null) {
		// If any hard error occurs while applying policies then make the activity result abort
		if(workflow.scratchpad.approvalPayload.error) {
			activity.result = 'abort';
			createCloudTrail('Apply Policy', '4', true, 'Unexpected error from policy engine.', workflow.scratchpad.approvalPayload);
		}
		if(workflow.scratchpad.approvalPayload.abortProcess) {
			activity.result = 'abort';
			createCloudTrail('Apply Policy', '4', true, 'Aborting Blueprint Request WF: ' + workflow.scratchpad.approvalPayload.message, workflow.scratchpad.approvalPayload);
		}
		// If any of the policy rules gets applied then successful is true.
		else if(workflow.scratchpad.approvalPayload.successful) {
			var approval = {};
			
			// For serviceNow approvals, approval payload will always be generated 
			if(workflow.scratchpad.approvalPayload.approval != null) {
				approval = workflow.scratchpad.approvalPayload.approval;
				if(approval.managerApproval || approval.userApproval || approval.groupApproval){
					invokeApprovalWFPolicy = true;
				}
			} else if(workflow.scratchpad.approvalPayload.workflowId != null) {
				// For custom approvals, approval payload is null and workflow id always generated
				invokeApprovalWFPolicy = true;
			}
			
			if(!gs.nil(workflow.scratchpad.approvalPayload.logs)) {
				var logs = new global.JSON().decode(workflow.scratchpad.approvalPayload.logs);
				current.description = logs.message + '';
				createCloudTrail('Apply Policy Rule Action', '2', true, 'Triggering approval WF: ' + logs.message, logs.rules);
			}
		}

		workflow.info(workflow.scratchpad.approvalPayload.message);
	}

	if(invokeApprovalWFPolicy) {
		nextFlow = policyUtil.getWorkflowName(workflow.scratchpad.approvalPayload.workflowId + '');
		// If policy does not return approval WF then fall back to default WF
		if(nextFlow == '') {
			nextFlow = new global.WorkflowChooser().chooseWorkflowForAction('cloud_approval', current);
			if (nextFlow == '') {
				workflow.info("Approval workflow could not be found");
				createCloudTrail('Apply Policy', '4', true, 'Blueprint Request WF: Approval workflow could not be found', '');
				activity.result = 'abort';
			}
			else
				activity.result = 'success';
		}
		else
			activity.result = 'success';
		workflow.scratchpad.approvalWF = nextFlow + '';
	}		
}

function createCloudTrail(step, messageType, milestone, messageSummary, messageDetails) {
	var catalogItemId = '' + current.cat_item;
	var requestItemId = '' + current.sys_id;
	var orderId ='';
	var contextKey = '';
	var stackId = '';
	var invocationTemplateId = '' + current.cat_item.blue_print;
	var targetInstanceId = '';
	var userId = '' + current.request.requested_for;
	var groupId = '';
	/* TODO: orderFormData is missing. var groupId'' + workflow.scratchpad.payload.orderFormData.UserGroup; */
	var trailId = new AddCloudTrail().createTrailRecord(catalogItemId, requestItemId, orderId, contextKey, stackId, invocationTemplateId, targetInstanceId, messageType, milestone, messageDetails, messageSummary, step, userId, groupId);
}

