function ifScript() {
	if(!gs.nil(workflow.scratchpad.approvalResult)) {
		if(workflow.scratchpad.approvalResult + '' == 'approved') {
			return 'yes';
		}
		else if(workflow.scratchpad.approvalResult + '' == 'rejected')
			return 'no';
		else
			return 'error';
	}
	else 
		return 'error';
}

