function updateDescription() {

	var grTaskId = workflow.scratchpad.taskSysId;
	if(gs.nil(grTaskId))
		return;
	
	var grTask = new GlideRecord('sc_task');
	if(!grTask.get(grTaskId))
		return;
	
	
	var taskState = grTask.state;
	var inputs = {};
	inputs = new global.JSON().decode(current.variables.cloudTaskWorkflowInput);

	// Get latest error description from the order if order is created.
	//var orderId = '' + inputs.orderId;

	var orderId = '' + workflow.scratchpad.orderId;

	if(gs.nil(orderId))
		return;

	var grOrder = new GlideRecord('sn_cmp_order');
	if (grOrder.get(orderId)) {
		var descr = '' + grOrder.status_msg;

		// Update req item description; current is req item
		new global.CmpSCUtil().setJournalEntry(current, true, true, descr.split('\n')[0]);

		// Update description on task and also worknotes
		grTask.description = gs.getMessage('Error Details: {0}',descr);
		grTask.short_description = gs.getMessage('{0}', descr.split('.')[0]);
		new global.CmpSCUtil().setJournalEntry(grTask, true, true, descr.split('\n')[0]);
	}
}

